import React, { useState, useEffect } from "react";
import "./App.css";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";

const App = () => {
  const [city, setCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [hourlyData, setHourlyData] = useState([]);
  const [error, setError] = useState("");

  const apiKey = "YOUR_WEATHERBIT_API_KEY";  // Replace with your API key from Weatherbit

  const getWeather = async (city) => {
    setError("");
    try {
      const response = await fetch(
        `https://api.weatherbit.io/v2.0/current?city=${city}&key=${apiKey}`
      );
      const data = await response.json();
      if (data && data.data && data.data.length > 0) {
        setWeatherData(data.data[0]);
      } else {
        setError("City not found. Please try again.");
      }
    } catch (error) {
      setError("Error fetching weather data. Please try again later.");
    }
  };

  const getForecast = async (city) => {
    setError("");
    try {
      const response = await fetch(
        `https://api.weatherbit.io/v2.0/forecast/daily?city=${city}&key=${apiKey}`
      );
      const data = await response.json();
      if (data && data.data) {
        setForecastData(data.data);
        setHourlyData(data.data[0].hourly);  // You can adjust this for more accurate hourly data
      } else {
        setError("Forecast data not found. Please try again.");
      }
    } catch (error) {
      setError("Error fetching forecast data. Please try again later.");
    }
  };

  const handleSearch = () => {
    if (city.trim() !== "") {
      getWeather(city);
      getForecast(city);
    }
  };

  useEffect(() => {
    if (city === "") return;
    getWeather(city);
    getForecast(city);
  }, [city]);

  return (
    <div className="app">
      <div className="search">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city name"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {error && <div className="error">{error}</div>}

      {weatherData && (
        <div className="current-weather">
          <h2>{weatherData.city_name}</h2>
          <div>
            <p>Temperature: {weatherData.temp}Â°C</p>
            <p>Humidity: {weatherData.rh}%</p>
            <p>Wind Speed: {weatherData.wind_spd} m/s</p>
          </div>
        </div>
      )}

      {forecastData && (
        <div className="forecast">
          <h2>7-Day Forecast</h2>
          <div className="forecast-cards">
            {forecastData.slice(0, 7).map((day, index) => (
              <div className="forecast-card" key={index}>
                <p>{new Date(day.datetime).toLocaleDateString()}</p>
                <p>High: {day.temp_max}Â°C</p>
                <p>Low: {day.temp_min}Â°C</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {hourlyData.length > 0 && (
        <div className="hourly-chart">
          <h2>Today's Hourly Weather</h2>
          <Line
            data={{
              labels: hourlyData.map((data) =>
                new Date(data.timestamp_utc).toLocaleTimeString()
              ),
              datasets: [
                {
                  label: "Temperature (Â°C)",
                  data: hourlyData.map((data) => data.temp),
                  fill: false,
                  borderColor: "rgba(75,192,192,1)",
                  tension: 0.1,
                },
              ],
            }}
          />
        </div>
      )}
    </div>
  );
};

export default App;
