import React, { useState, useEffect } from 'react';
import './App.css';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Registering Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [city, setCity] = useState('Gopalganj');
  const [weather, setWeather] = useState(null);
  const [hourly, setHourly] = useState([]);
  const [daily, setDaily] = useState([]);
  const [error, setError] = useState(null);

  const apiKey = '71c966b370b44cc4a708548ef957b5f2'; // Your WeatherAPI key
  const url = `https://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${city}&hours=24&days=7`;

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(url);
        const data = await response.json();

        if (data.error) {
          setError(data.error.message);
          return;
        }

        setWeather({
          city: data.location.name,
          temperature: data.current.temp_c,
          condition: data.current.condition.text,
          humidity: data.current.humidity,
          wind: data.current.wind_kph,
          icon: `https:${data.current.condition.icon}`,
        });

        setHourly(data.forecast.forecastday[0].hour);
        setDaily(data.forecast.forecastday);

      } catch (error) {
        setError('Failed to fetch weather data');
      }
    };

    fetchWeatherData();
  }, [city]);

  const handleSearch = () => {
    setError(null);
    setHourly([]);
    setWeather(null);
    if (city.trim() !== '') {
      fetchWeatherData();
    }
  };

  const hourlyData = hourly.map(hour => hour.temp_c);
  const hourlyLabels = hourly.map(hour => `${hour.time.slice(11, 16)}`);

  const dailyData = daily.map(day => day.day.avgtemp_c);
  const dailyLabels = daily.map(day => new Date(day.date).toLocaleDateString());

  const data = {
    labels: hourlyLabels,
    datasets: [
      {
        label: 'Temperature (°C)',
        data: hourlyData,
        fill: false,
        borderColor: '#4e73df',
        tension: 0.1,
      },
    ],
  };

  const dailyDataForChart = {
    labels: dailyLabels,
    datasets: [
      {
        label: 'Average Temperature (°C)',
        data: dailyData,
        fill: false,
        borderColor: '#ff5733',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="App">
      <div className="search">
        <input 
          type="text" 
          placeholder="Enter city name" 
          value={city} 
          onChange={e => setCity(e.target.value)} 
          className="search-input"
        />
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>

      {error && <div className="error">{error}</div>}

      {weather && (
        <div className="weather-info">
          <h1>{weather.city}</h1>
          <img src={weather.icon} alt="Weather Icon" className="weather-icon" />
          <p className="temperature">{weather.temperature}°C</p>
          <p className="condition">{weather.condition}</p>
          <p>Humidity: {weather.humidity}%</p>
          <p>Wind: {weather.wind} km/h</p>

          <div className="chart-container">
            <h2>Hourly Forecast (Next 24 Hours)</h2>
            <Line data={data} />
          </div>

          <div className="chart-container">
            <h2>7-Day Forecast</h2>
            <Line data={dailyDataForChart} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
