import React, { useState, useEffect } from 'react';
import './App.css';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Registering Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [city, setCity] = useState('Gopalganj');
  const [weather, setWeather] = useState(null);
  const [hourly, setHourly] = useState([]);
  const [error, setError] = useState(null);

  const apiKey = '71c966b370b44cc4a708548ef957b5f2';  // Your new API key
  const url = `https://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${city}&hours=24`;

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(url);
        const data = await response.json();

        if (data.error) {
          setError(data.error.message);
          return;
        }

        setWeather({
          city: data.location.name,
          temperature: data.current.temp_c,
          condition: data.current.condition.text,
          humidity: data.current.humidity,
          wind: data.current.wind_kph,
          icon: `https:${data.current.condition.icon}`,
        });

        setHourly(data.forecast.forecastday[0].hour);
      } catch (error) {
        setError('Failed to fetch weather data');
      }
    };

    fetchWeatherData();
  }, [city, apiKey]);

  const handleSearch = () => {
    setError(null);
    setHourly([]);
    setWeather(null);
    if (city.trim() !== '') {
      fetchWeatherData();
    }
  };

  const hourlyData = hourly.map(hour => hour.temp_c);
  const hourlyLabels = hourly.map(hour => `${hour.time.slice(11, 16)}`);

  const data = {
    labels: hourlyLabels,
    datasets: [
      {
        label: 'Temperature (°C)',
        data: hourlyData,
        fill: false,
        borderColor: '#4e73df',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="App">
      <div className="search">
        <input 
          type="text" 
          placeholder="Enter city name" 
          value={city} 
          onChange={e => setCity(e.target.value)} 
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {error && <div className="error">{error}</div>}

      {weather && (
        <div className="weather-info">
          <h1>{weather.city}</h1>
          <img src={weather.icon} alt="Weather Icon" />
          <p>{weather.temperature}°C</p>
          <p>{weather.condition}</p>
          <p>Humidity: {weather.humidity}%</p>
          <p>Wind: {weather.wind} km/h</p>

          <div className="chart-container">
            <Line data={data} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
